// Program for OCA-Client sideimport java.net.Socket;import java.io.InputStreamReader;import java.io.BufferedReader;import static java.lang.System.out;import java.io.PrintStream;import java.util.Scanner;class OCAClient{	static public void main(String...args) throws Exception	{		Scanner sc=new Scanner(System.in);		System.out.println("Enter IP address to chat: ");		String ipAddress=sc.next();		Socket socket=new Socket(ipAddress,2000);		InputStreamReader isr=new InputStreamReader(System.in);		BufferedReader br=new BufferedReader(isr);		PrintStream ps=new PrintStream(socket.getOutputStream());		while(true)		{			String data=br.readLine();			if(data==null)				break;			ps.println(data);			}	}}