// Program for OCA-Server sideimport java.net.Socket;import java.net.ServerSocket;import java.io.InputStreamReader;import java.io.BufferedReader;import static java.lang.System.out;class OCAServer{	static public void main(String...args) throws Exception	{		ServerSocket ss=new ServerSocket(2000);		Socket socket=ss.accept();		InputStreamReader isr=new InputStreamReader(socket.getInputStream());		BufferedReader br=new BufferedReader(isr);		while(true)		{			String data=br.readLine();			if(data.length()==0)				break;			out.println(data);		}	}}